        </svg>
      </div>
      <div class="header__panel">
        <div class="dropdown"
             @click="toggleDropdown"
             :class="{'dropdown_open' : dropdownVisible}"
        >
          <button class="dropdown__button" type="button">
            <span>
              {{ activeLang }}
            </span>
            <svg class="icon icon_downup dropdown__icon" height="1em" width="0.9230769230769231em">
              <use xlink:href="#icon-downup"></use>
            </svg>
          </button>
          <nav class="dropdown__links">
            <button
                class="dropdown__link"
                v-for="lang in languages"
                :key="lang.locale"
                @click="setActiveLang(lang.name, lang.locale)"
            >
              {{ lang.name }}
            </button>
          </nav>
        </div>
