<script>
import {ref} from "vue"
import {useI18n} from 'vue-i18n'

export default {
  name: "TheHeader",
  setup() {
    const dropdownVisible = ref(false);

    function toggleDropdown() {
      dropdownVisible.value = !dropdownVisible.value;
    }

    const {locale} = useI18n();
    let languagesArr = ref([
      {name: 'Eng', locale: 'en'},
      {name: 'Rus', locale: 'ru'},
      {name: 'Ger', locale: 'ger'},
      {name: 'Ita', locale: 'ita'}
    ])
    let languages = ref([]);
    const activeLang = ref('Eng');

    languages.value = languagesArr.value.filter(el => el.name != activeLang.value);

    function setActiveLang(name, lang) {
      languages.value = languagesArr.value.filter(el => el.name != name);

      activeLang.value = name;
      locale.value = lang
    }

    function scrollTo(selector) {
      document.querySelector(selector).scrollIntoView({block: "start", behavior: 'smooth'});
    }


    return {
      dropdownVisible,
      toggleDropdown,
      languages,
      setActiveLang,
      activeLang,
      scrollTo
    }
  }
}
</script>

